<?php/** * Template Name: Package pricing * * @package Templates * @version 1.0.0 */if ( ! class_exists( 'pbs_page_template_faq' ) ) {	class pbs_page_template_faq extends pbs_page_template {		/**		 * Additional classes for main wrapper		 *		 * @var array		 */		public $classes = array( 'page-default' );		/**		 * Additional classes for main container		 *		 * @var array		 */		public $container_classes = array( 'container container-wrapped container-packages' );		public function header() {			if ( class_exists( 'Lisner_Core' ) ) {				include lisner_helper::get_template_part( 'header-media', 'pages/header' );			} else {				$this->classes = array( 'page-default page-default-unit' );			}		}		/**		 * Main Content rendering		 */		public function main_content() {			global $post;			$has_sidebar = is_active_sidebar( 'sidebar-page' );			the_post();			$product = wc_get_products(array('category' => array('packages'),));// Now you have access to (see above)...			//var_dump($product);			?>				<div class="row row-wrapper">					<div class="col-sm-12">						<section class="main-content">							<div class="row lisner-packages">								<div class="listing-packages">			<?php			$x=0;			foreach($product as $item_detail)			{			$x+=1;			?>										<div class="col-md-4 p-0">											<div class="listing-package-wrapper  <?php echo ($x==2?"listing-package-wrapper-distinctive":null); ?>">												<div class="listing-package">													<!-- Listing Package / Title -->													<div class="listing-package-title">														<p><?php echo $item_detail->get_name(); ?></p>														<h2 class=""><span class="woocommerce-Price-amount amount"><span class="woocommerce-Price-currencySymbol">R</span><?php echo $item_detail->get_regular_price(); ?></span></h2>													</div>													<!-- Listing Package / Description -->													<div class="listing-package-description"><?php echo $item_detail->get_limit(); ?><strong> standard</strong> listing submission active for <?php echo $item_detail->get_duration(); ?> days!</div>													<!-- Listing Package / Content -->													<div class="listing-package-content">														<ul class="list-unstyled">															<li><i class="material-icons color-secondary mf">check_circle_outline</i><strong><?php echo $item_detail->get_limit(); ?></strong> Listing</li>															<li><i class="material-icons color-secondary mf">check_circle_outline</i><strong><?php echo $item_detail->get_duration(); ?> Days</strong> Availability</li>															<li><i class="material-icons color-secondary mf">check_circle_outline</i>																<strong><?php echo ($item_detail->is_job_listing_featured()?  "Featured": "Standard"); ?> </strong> <?php echo ($item_detail->is_job_listing_featured()?  "In The Results": "Listing"); ?></li>														</ul>													</div>													<a href="javascript:" class="package-call btn btn-primary <?php echo ($x!=2?"btn-primary-bordered":null); ?> " >Start now</a>													<input class="hidden" type="radio" checked="checked" name="job_package" value="40" id="package-40">												</div>											</div>										</div>			<?php			}?>									</div>								</div>							</section>						</div>					</div>		<?php		}	}}new pbs_page_template_faq();